Compose_-_M365_Query.inputs:
let accountData = @'@{replace(string(body('Select_-_Accounts_Array')), '''', '''''')}';
let ipData = @'@{replace(string(body('Select_-_IP_Array')), '''', '''''')}';
let hostData = @'@{replace(string(body('Select_-_Hosts_Array')), '''', '''''')}';
let accountEntities = datatable(temp:string)['']
| extend Entities = parse_json(accountData)
| mv-expand todynamic(Entities)
| project UserPrincipalName = tostring(Entities.userPrincipalName), SamAccountName = tostring(Entities.onPremisesSamAccountName), ObjectSID = tostring(Entities.onPremisesSecurityIdentifier), AADUserId = tostring(Entities.id), ManagerUPN = tostring(Entities.managerUPN);
let ipEntities = datatable(temp:string)['']
| extend Entities = parse_json(ipData)
| mv-expand todynamic(Entities)
| project IPAddress = tostring(Entities.Address), Latitude = todecimal(Entities.Latitude), Longitude = todecimal(Entities.Longitude), Country=tostring(Entities.Country), State=tostring(Entities.State);
let hostEntities = datatable(temp:string)['']
| extend Entities = parse_json(hostData)
| mv-expand todynamic(Entities)
| project FQDN = tostring(Entities.FQDN), Hostname = tostring(Entities.Hostname);
@{triggerBody()?['KQLQuery']}

