Compose.inputs:
let incidentARMId = '@{body('Parse_JSON')?['IncidentARMId']}';
let alertData = @'@{body('Select_-_Alert_Info')}';
let relatedRules = @'@{body('HTTP_-_Get_Incident')?['properties']?['relatedAnalyticRuleIds']}';
let accountData = @'@{replace(string(body('Select_-_Account_Entities')), '''', '\''')}';
let ipData = @'@{replace(string(body('Parse_JSON')?['IPs']), '''', '\''')}';
let hostData = @'@{replace(string(body('Parse_JSON')?['Hosts']), '''', '\''')}';
let checkAccountEntity = @{triggerBody()?['CheckAccountEntityMatches']};
let checkIPEntity = @{triggerBody()?['CheckIPEntityMatches']};
let checkHostEntity = @{triggerBody()?['CheckHostEntityMatches']};
let lookback = @{triggerBody()?['LookbackInDays']}d;
let severityOrder = datatable (AlertSeverity:string, Order:int)['Informational', 1, 'Low', 2, 'Medium', 3, 'High', 4];
let isFusionIncident = toscalar(datatable (temp:string)['']
| extend analyticRules = relatedRules
| extend fusion = iff(analyticRules has 'BuiltInFusion', true, false)
| project fusion);
let accountEntities = toscalar(datatable(temp:string)['']
| where checkAccountEntity
| extend Entities = parse_json(accountData)
| mv-expand todynamic(Entities)
| project SamAccountName=Entities.onPremisesSamAccountName, SID=Entities.onPremisesSecurityIdentifier, UPNName=split(Entities.userPrincipalName,'@')[0], Friendly=Entities.friendlyName
| extend EntityData = pack_array(SamAccountName, SID, UPNName, Friendly)
| mv-apply EntityData on (where isnotempty(EntityData))
| summarize EntityData=make_set(EntityData));
let ipEntities = toscalar(datatable(temp:string)['']
| where checkIPEntity
| extend Entities = parse_json(ipData)
| mv-expand todynamic(Entities)
| project IP= coalesce(Entities.Address, Entities.RawEntity.address, Entities.RawEntity.friendlyName)
| where isnotempty( IP)
| summarize EntityData=make_set(IP));
let hostEntities = toscalar(datatable(temp:string)['']
| where checkHostEntity
| extend Entities = parse_json(hostData)
| mv-expand todynamic(Entities)
| project FQDN=Entities.FQDN, Hostname=coalesce(Entities.Hostname, Entities.RawEntity.hostName, Entities.RawEntity.friendlyName)
| extend EntityData = pack_array(FQDN, Hostname)
| mv-apply EntityData on (where isnotempty(EntityData))
| summarize EntityData=make_set(EntityData));
let currentIncidentAlerts = datatable (temp:string)['']
| extend alertData = todynamic(alertData)
| mv-expand alertData
| project alertData.systemAlertId;
SecurityAlert 
| where TimeGenerated > ago(lookback) 
| summarize arg_max(TimeGenerated, *) by SystemAlertId 
| where SystemAlertId !in (currentIncidentAlerts) or isFusionIncident
| mv-expand todynamic(Entities) 
| where Entities has_any (accountEntities) or ( Entities has_any (ipEntities) and Entities.Type == "ip") or Entities has_any (hostEntities) or (SystemAlertId in (currentIncidentAlerts) and isFusionIncident)
| extend AccountEntityMatch = iff(Entities has_any (accountEntities), true, false), HostEntityMatch = iff(Entities has_any (hostEntities), true, false), IPEntityMatch = iff(Entities has_any (ipEntities) , true, false) 
| summarize AccountEntityMatch = max(AccountEntityMatch), IPEntityMatch=max(IPEntityMatch),HostEntityMatch=max(HostEntityMatch) by StartTime, DisplayName, AlertSeverity, SystemAlertId, ProviderName, Tactics
| join kind=leftouter severityOrder on AlertSeverity
| sort by Order desc
| project-away Order, AlertSeverity1
